/*
The following script define a method
that increments an integer by 1 
and registers it as a UDF with 
the SparkSession, which can be accessed
using the variable named "spark":
def inc(i: Integer): Integer = {
  i + 1
}
spark.udf.register ("inc", inc _)

*/

global sdc

DROP = ("what=", "#pipeline_id=", "#pipeline_type=", "#source=", "#source_host_id=", "#source_host_name=")
PRIORITIES = {
    'info': 'low',
    'low': 'low',
    'medium': 'normal',
    'high': 'high',
    'critical': 'urgent',
}


def common_dimensions(record):
    common_dimensions = {}
    for alert in record.value["alerts"]:
        for metric in alert["metrics"]:
            dimensions = filter(
                lambda x: not x.startswith(DROP),
                metric["name"].split('.')
            )
            for dim in dimensions:
                if dim not in common_dimensions:
                    common_dimensions[dim] = 1
                else:
                    common_dimensions[dim] += 1
    return [k for k, v in common_dimensions.iteritems() if v > 1]


def name(metric):
    name = metric["name"].split('.')[0].split('=')
    return name[1] if len(name) == 2 else name[0]


def _is_up(metric):
    return metric["direction"] == "UP"


def direction_type(metric):
    return "Spike" if _is_up(metric) else "Drop"


def percentage_delta(metric):
    if _is_up(metric):
        if "upperPercentageDelta" in metric:
            return str(metric["upperPercentageDelta"])
    else:
        if "lowerPercentageDelta" in metric:
            return str(metric["lowerPercentageDelta"])
    return ''


def dimensions(metric, common_dims):
    dims = filter(
        lambda x: not x.startswith(DROP),
        metric["name"].split('.')
    )
    dims = filter(
        lambda x: x not in common_dims,
        dims
    )
    return filter(
        lambda x: not str(x).isnumeric(),
        dims
    )


def state(alert):
    for metric in alert["metrics"]:
        if metric["state"] == "OPEN":
            return "Open"
    return "Close"


for record in sdc.records:
    message = ''
    subject = record.value['subject']
    priority = PRIORITIES[record.value['severity']]

    common_dims = common_dimensions(record)
    if common_dims:
        message += "Common dimensions: " + ", ".join(common_dims) + "\n"
    for alert in record.value["alerts"]:
        sdc.log.info("Input record: " + str(record.value))
        message += "Alert " + state(alert) + ": " + alert["title"] + "\n"
        if "type" in alert and alert["type"]:
            message += "*Type:* " + alert["type"]
        if "description" in alert and alert["description"]:
            message += alert["description"] + "\n"
        message += "___________________________\n"
        for metric in alert["metrics"]:
            if "direction" in metric:
                message += direction_type(metric) + " in " + name(metric) + "\n"
            if "from" in metric:
                message += "`" + percentage_delta(metric) + "` (from " + str(
                    metric["from"]) + " to " + str(metric["to"]) + ")\n"
            if "duration" in metric and "startTime" in metric:
                message += "*Duration:* `" + metric["duration"] + " (started " + metric["startTime"] + ")`\n"
            message += "*Dimensions:* `" + ", ".join(dimensions(metric, common_dims)) + "`\n"
            message += "___________________________\n"
        for metric in alert["metrics"]:
            if "imageUrl" in metric and metric["imageUrl"]:
                message += metric["imageUrl"] + "\n"
                break
        if 'investigationUrl' in record.value and record.value['investigationUrl']:
            message += record.value["investigationUrl"] + "\n"
        if "alertSettingsUrl" in alert:
            message += alert["alertSettingsUrl"]

    data = {
        'variables': {
            'subject': subject,
            'description': message,
            'priority': priority
        }
    }

    new_record = sdc.createRecord('Anodot alert')
    new_record.value = data
    try:
        sdc.log.info("Output record: " + str(new_record.value))
        sdc.output.write(new_record)
    except Exception as e:
        sdc.error.write(new_record, str(e))
